handle_file_ln "$HOME/.dotfiles/bin/init-github-remote" "$HOME/bin/init-github-remote"
handle_file_ln "$HOME/.dotfiles/bin/install.sh" "$HOME/bin/install.sh"
handle_file_ln "$HOME/.dotfiles/bin/motd" "$HOME/bin/motd"
handle_file_ln "$HOME/.dotfiles/config/fish/config.fish" "$HOME/.config/fish/config.fish"
handle_file_ln "$HOME/.dotfiles/dircolors" "$HOME/.dircolors"
handle_file_ln "$HOME/.dotfiles/env" "$HOME/.env"
handle_file_ln "$HOME/.dotfiles/env.personal" "$HOME/.env.personal"
handle_file_ln "$HOME/.dotfiles/env.work" "$HOME/.env.work"
handle_file_ln "$HOME/.dotfiles/gitconfig" "$HOME/.gitconfig"
handle_file_ln "$HOME/.dotfiles/inputrc" "$HOME/.inputrc"
handle_file_ln "$HOME/.dotfiles/rcrc" "$HOME/.rcrc"
